import osimport numpy as npimport soundfile as sfduration = 50directoryPath = 'C:/Users/dwigh/PycharmProjects/AudioProcessor/data'directoryFiles = os.listdir(directoryPath)# print(directoryFiles)for file in directoryFiles:    rawData = open('data/' + file)    # print(rawData)    length = len(open('data/' + file).readlines())    dataLine = rawData.readlines()    # print(len(dataLine))    # print(length)    sampleRate = length/duration    buffer = [0 for i in range(length)]    scaled = [0.0 for i in range(length)]    for values in range(0,length):        line = rawData.readline()        # print(line)        intAudioData = int()        buffer[values] = intAudioData        scaled[values] = float(buffer[values] * 2) / 1024    # print(scaled)    scaled = np.asarray(scaled)    # print(scaled)    file.replace('.txt','')    sf.write('output/' + file + '.wav', scaled, int(sampleRate))# from scipy.io.wavfile import write# import numpy as np# import soundfile as sf# import wave# rawData = open("data/audio-converted_000000001.txt")# print(rawData)# numSmaples = 80000# time = 12# sampleRate = numSmaples/time;# buffer = [0 for i in range(numSmaples)]# scaled = [0.0 for i in range(numSmaples)]# for values in range(0,numSmaples):#     line = rawData.readline()#     # print(line)#     intAudioData = int(line)#     buffer[values] = intAudioData#     scaled[values] = float(buffer[values] + 300) / 1024# # print(scaled)# scaled = np.asarray(scaled)# # print(scaled)# sf.write('DUT.wav', scaled, int(sampleRate))